insert  into `bk_admin`(`id`,`user`,`email`,`password`,`repassword`,`create_time`,`update_time`,`delete_time`,`ip`) values (1,'admin',NULL,'21232f297a57a5a743894a0e4a801fc3','',NULL,NULL,NULL,NULL),(2,'abc','931274989@qq.com','670b14728ad9902aecba32e22fa4f6bd','670b14728ad9902aecba32e22fa4f6bd','2018-12-20 18:02:15','2018-12-20 18:02:15',NULL,NULL);

/*Table structure for table `bk_article` */

DROP TABLE IF EXISTS `bk_article`;

CREATE TABLE `bk_article` (
  `id` mediumint(9) NOT NULL AUTO_INCREMENT COMMENT '文章id',
  `title` varchar(60) DEFAULT NULL COMMENT '文章标题',
  `keyword` varchar(100) DEFAULT NULL COMMENT '文章关键词',
  `desc` varchar(255) DEFAULT NULL COMMENT '文章描述',
  `author` varchar(30) DEFAULT NULL COMMENT '文章作者',
  `pic` varchar(255) DEFAULT NULL COMMENT '文章缩略图',
  `centent` text COMMENT '文章内容',
  `click` mediumint(9) DEFAULT '0' COMMENT '点击数',
  `zan` mediumint(9) DEFAULT '0' COMMENT '点赞数',
  `create_time` timestamp NULL DEFAULT NULL COMMENT '发布时间',
  `cateid` mediumint(9) DEFAULT NULL COMMENT '所属栏目',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=16 DEFAULT CHARSET=utf8;

/*Data for the table `bk_article` */

insert  into `bk_article`(`id`,`title`,`keyword`,`desc`,`author`,`pic`,`centent`,`click`,`zan`,`create_time`,`cateid`) values (3,'人生何尝不是一场赌局','生活,心情  ','最近同事们沉陷红群无法自拔，作为一个旁观者还是挺开心的，再一次见证多巴胺和肾上腺素这些让人爽到爆的物质，在日常生活中扮演着魔鬼般的角色。人从来都不是理智的动物，总是感觉一切都在 ……','author',NULL,'<p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 2em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, \">最近同事们沉陷红群无法自拔，作为一个旁观者还是挺开心的，再一次见证多巴胺和肾上腺素这些让人爽到爆的物质，在日常生活中扮演着魔鬼般的角色。人从来都不是理智的动物，总是感觉一切都在控制之中，心存侥幸心理。当我们陷入这种心理时，输赢已经不再重要了，只在乎一种感觉“快感”，对，和性交一样，性是一切的本源。由此看来，我这些同事们还是沉迷其中也是有原由的，他们都有一个共同点，婚姻不和谐，需要通过其它方式来满足快感，魔鬼和天使其实是一体两面的。</p><p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 2em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, \">春暖花开时，天气突然转冷，前天二十多度到二度的气温变化，让人不能适应，连小陈都失声了。在这个春天我必须做出一些选择，人有时候是需要逼自己一下的，否则当生活变的被动，剩下的只有叹息。<br/></p><p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 2em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, \">萧伯纳说“想结婚的就去结婚，相单身就维持单身，反正到最后你们都会后悔。”我就想知道他最后到底结没结婚？</p><p><br/></p>',0,0,'2018-12-20 22:21:16',4),(4,'2016年就一个字：戒！​','摄影,生活,心情,人生,觉悟','这条路小时候走了无数次，将来还会更多……','author','\\uploads/20181220\\2630792441356eef4ab354bf886fadcd.jpg','<p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 2em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\">太阳晒的人懒洋洋的，这段时间的汉中也和其它地方一样，迎来了冬季最冷的天气，零下7度左右，家里的水管都冻爆了。</p><p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 2em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\">很快过春节了，我们公司也和本地其它小公司一样，员工都在等待着放假和年终奖，完全不在工和状态。其实大家都没盼望着过年，这意味着在没有明确的计划之前，过完年又得重复这种百无聊赖的工作和生活，每个人都憋足了劲，又不知向何处使。</p><p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 2em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\">这个时候很多人都在写工作总结，什么A总结，B计划之类的，其实真没什么意义，我就不写了。这一年对我个人而言还算不错，变化挺大，也有惊喜，2016年就一个字：<strong>戒！</strong></p><p><br/></p>',1,0,'2018-12-20 22:23:58',4),(5,'初冬，景如故','生活,心情,人生,回忆  ','进入冬季，天很冷，时不时下点小雨，街上妖风阵阵，吹的人老想咳嗽，已经半个月没见阳光了。感觉自己就像对面楼顶的那棵树，每天随风摇摆，心无所住 ……','author',NULL,'<p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 2em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\">进入冬季，天很冷，时不时下点小雨，街上妖风阵阵，吹的人老想咳嗽，已经半个月没见阳光了。</p><p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 2em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\">路边金黄色的银杏树叶最终也没能忍住寒风的摧残，随风飘落，形成了一道美丽的风景。周六的下午大家都等着早点下班，每天一眼能看到头的工作，已经让很多人忘记了外面的世界到底有多精彩，聊起来可能更多的是无奈。<br/></p><p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 2em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\">思绪追回到多年前，那时个汉中冬天还是下雪的，穿着羽绒服裹着围巾穿梭在大街小巷，心情是欢乐的。那时候北大街还有望湖春的包子和北大宾馆，东大街还有夜市，我们都没有什么钱，生活的却很有品质。而今，人越成长越孤单，虽然还是没什么钱，也开始追求生活品质，却怎么也追不上，能做的也只能让自己的心境越来越淡泊，也不知道这是好是坏。</p><p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 2em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\">在这里工作已五年有余，人生六分之一时间都是坐在公司的这个地方面对着窗外，不知道自己是怎么坚持下来的，感觉自己就像对面楼顶的那棵树，每天随风摇摆，心无所住 。<br/></p><p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 2em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\">下班了，下回再聊……</p><p><br/></p>',2,0,'2018-12-20 22:25:02',4),(6,'见路不走，清明','生活,过往,心情,摄影  ','无论是面对厄运还是幸运都应该保持平常心，喜怒伤身，情绪伤神，执念误一生。医生笑着说这是他从医以来，见到的为数不多的案例，可谓是奇迹，而我的心却很平静，至此才真正领悟到了无为和不争。','author','\\uploads/20181220\\53181397fcae03b971f04ad77108aefe.jpg','<p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 2em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\">今年的第一场暴雨来的有些急，与过去经历的很多事情一样让人不知所措，伴随着咆哮的雷声，今年又过去了四分之一。</p><p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 2em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\">五年前的四月，一位好友离开了这个对他来说有些拥挤的世界，带着他那未酬的壮志了却此生，他是被逼无奈还是真的参破生死，如今依然不得而知。不管是什么，我将要开始我新的人生了，带着缅怀的心情做一些事情。</p><p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 2em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\">十年前的这个季节，生活出现了一些波澜，成为我这一生的转折点，十年后的今天，又转回来了，世事就是这样周而复始。</p><p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 2em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\">如今已经彻底摆脱了这位多年来如影随形的朋友，还真有些不习惯。之所以称之为朋友，因为有它，让我经历大多数人一生都不会有的遭遇，和成功战胜它的体验。无论是面对厄运还是幸运都应该保持平常心，喜怒伤身，情绪伤神，执念误一生。医生笑着说这是他从医以来，见到的为数不多的案例，可谓是奇迹，而我的心却很平静，至此才真正领悟到了无为和不争。</p><p><br/></p>',0,0,'2018-12-20 22:26:10',4),(7,'山花烂漫的季节，我们各自冷暖自知','生活,过往,回忆,心情  ','时间久了，就对节日渐渐的没有了喜悦感，反而会更加是适应这种状态，在无声无息的岁月里挣扎着，徘徊着，就像一位弱视者伸着手步履蹒跚的向前走着，眼前有光和影子，却始终也看不清……','author',NULL,'<p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 2em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\">今日便是立春，每一年中的第一个节气，又是一个山花烂漫的季节，我们各自冷暖自知！</p><p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 2em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\">时间久了，就对节日渐渐的没有了喜悦感，反而会更加是适应这种状态，在无声无息的岁月里挣扎着，徘徊着，就像一位弱视者伸着手步履蹒跚的向前走着，眼前有光和影子，却始终也看不清。</p><p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 2em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\">冬季就在这一天结束了，这个冬天汉中下了一场雪，虽然是人工降的，可还是很美的。有的时候就是会因为一件小小的事情或一次短暂的相逢整个季节都变的不一样，人真是一个奇怪的动物，总是口是心非。</p><p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 2em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\">这两年拍的照片少了，零零散散的拍两张也都是草木风景，可有可无。不知从什么时候开始我的镜头里就很少出现人物，以至于静物拍的太多，整个人都变的静了，静的像一棵肆意生长树。</p><p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 2em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\">总是向往去一些地方，和舒服的人出去走走，或近或远都无所谓……</p><p><br/></p>',24,0,'2018-12-20 22:26:49',4),(8,'放下，放不下','心情,过往,生活  ','记忆就像望湖春的包子，时隔经年还是同样的味道 ……','author',NULL,'<p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 2em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\">不经意间，2014悄然而逝，今年就这么云淡风轻的过去了，依旧无悲无喜。</p><p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 2em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\">按常理说我应该庆幸才对，这么多年让亲人担忧的事情已经趋向好的方向发展，然而我却陷入了更深的疑惑。或许是因为这十年中各种的机缘巧合，错过了生活中很多本来美好的人和事物，这种把人性的弱点归结给命运的说法似乎很牵强，至少我自己无法接受。既然命运与我开了个不大不小的玩笑，我是否也应该还以颜色？好吧，我承认自己又着相了。</p><p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 2em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\">这一年感觉自己变的俗不可耐，与之前那个从不家长里短的人截然相反，尽管这也是真实的自我，偶尔也会感到快乐，可毕竟放纵的快乐不会长久，我厌恶这样的状态，想要改变却很难，希望有未来的日子里可以真正做到断舍离。</p><p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 2em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\">记忆就像望湖春的包子，时隔经年还是同样的味道，</p><p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 2em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\">冬天是个浪漫的季度，但愿今年可以见到白雪纷飞&nbsp; 　……</p><p><br/></p>',4,0,'2018-12-20 22:28:01',4),(9,'心乱无题','心情,回忆,生活  ','当时就被眼前这一幕感动了，我缓慢的走过，然后一路沉默……','author',NULL,'<p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 2em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\">刚才去客户公司回来的途中一只狗卧在路边，后腿流着血，显然是刚被车撞伤无力行走，旁边两只不同颜色的狗围着他打转，不时向路边的行人观望叫嚷。当时就被眼前这一幕感动了，我缓慢的走过，然后一路沉默。</p><p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 2em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\">这个秋天转瞬即逝，气温骤降都不知道穿什么了，长久以来，这个泛黄的季节都被涂上一层伤感的色彩，而我更多感受到的是寂静，风雨欲来的寂静。</p><p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 2em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\">这段时间以前很少出去运动，体重增加了不少，以往在这个季节是很喜欢去江边或是田间散步的，偶尔焚烧秸秆的烟雾会熏的人眼睛酸，可心里是快乐的。</p><p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 2em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\">不知道怎么形容现在百无聊赖的日子，很多中情看的清晰了，也就对它失去了热情。曾经追求的感情现在现在也不在那么执着了，只是默默的惦念着，曾经喜欢的东西如今也不怎么喜欢了，从不会主动的拿起。这样的习惯是受到周围人慵懒的习惯所影响，还是我一直还在等待？我希望是前者，因为它可以改变。</p><p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 2em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\">上次休假去了成都，意犹未尽，也再一次感受到出去旅行什么风景真的不那么重要，关键是和对的人一起，否则不如独自出行。</p><p><br/></p>',7,0,'2018-12-21 17:20:36',5),(10,'旅行，一件没有意义的事','人生,觉悟,旅行  ','旅行本身没有意义，就像读书，学习，提问，交流一样，这些事情本身都没有意义……\r\n\r\n','author',NULL,'<p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 2em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\">一个人但凡有些理想主义情怀，她的意愿清单上总会有一条叫做“环游世界”。我也有，并且曾经一度想要把它作为我的终极大目标，也曾经非常严肃地想过怎么样才能像旅游卫视的史林子一样做一个旅游节目主持人，玩了又赚了钱，一辈子过得潇洒。可后来当我知道这些主持人为了拍摄一个好镜头要连着吃八个馒头的时候，这个念头就打消了。不过，我心中那个背着大包，戴着墨镜，梳着一根马尾辫的女孩子一直目光坚定地朝着远方走去。</p><p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 2em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\">现在人们对旅行的极度推崇很大程度上是一种狂潮，为什么要旅游，因为旅游“好”。好在哪里？我不知道有多少人对这个问题有清楚的答案。对于缺时间又缺钱的大多数学生和上班族，“去远方”（是的，旅行这两个字不够文艺）是一个自己心中的乌托邦，更多的是对于枯燥乏味的现实生活的逃避，所以旅游更多的是”出去玩“和”散心“。所以有人开玩笑说，旅行就是从一个你呆腻的地方到别人呆腻的地方。</p><p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 2em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\">我曾经非常严肃的思考过人为什么要旅行，并且得到了一个我现在依旧认同的答案——这是一种不可替代的丰富生命的方法。我总觉得，好不容易活这么一次，如果不把这个世界走一走，一辈子就呆在屈指可数的几个地方，实在是有一种亏了的感觉。所以，为了把生命的本赚回来，怎么着也要多出去看看。</p><p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 2em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\">可是，”看看”就可以丰富生命了吗？当然，如果你可以旅居或者留学，那么对这个地方的收获会几何倍数增加。可是更多的人出游只能是小长假或者寒暑假，这种旅游更多的还是看风景，很少有机会接触到当地人。剑波那天提了一个问题，一个人去旅行，不过是在这个地方看看美景吃吃美食，对于这个地方的风土人情并没有机会做十分深入的了解，这样子的话有意义吗？</p><p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 2em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\">我认为有的。那些风景本身就是意义。同样都是居民楼，上海的和吉林的就不一样，北京和纽约的也不一样，她的砖瓦质地，建筑结构，颜色搭配，这本身就蕴含了丰富的艺术，是多少人生命的印记。按理来说，维基百科和书已经把世界各地风土人情都介绍遍了，一个人去旅游都不一定能够获得比这些资源给你多的知识。可是，正是因为知识获取如此便捷，纯知识的价值正在一点点降低，旅行绝不是为了知道。实际上大多数去看莫奈展的人根本不了解莫奈和印象派以及这个流派和宗教社会的关系，再说了网上都能看到莫奈的画作，人们大可以坐在电脑前点一点鼠标，不用去忍受地铁长时间的颠簸。可是，莫奈展的珍贵并不在于让人们知道有这么一幅画作，而是让人们感受到这幅画作的气息。我不知道怎么定义气息，只能说是除了直白的视觉体验之外的东西，不仅要运用到视觉，要调动你的全部感官去感受一幅画作。一幅画，当你站在他的面前，用自己的双眼直接看到它的线条，她的配色，感受到它整体的氛围，嗅到现场淡淡的油墨味道，这些东西是一幅画最珍贵的东西。或许你还不能像一个鉴赏家一样分析出个一二三四五，但是这幅画已经在你心中种下了一个种子，一旦阳光水分刚好，这颗种子就会发芽，长成参天大树，给你的人生遮挡毒辣的太阳。</p><p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 2em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\">旅行本身没有意义，就像读书，学习，提问，交流一样，这些事情本身都没有意义。如果一个人只是单纯的做它们，买一张张的机票着急的去下一个目的地，把书翻得哗啦啦响赶快看完一本又一本，不停地在微博上和大V对话争取一个面谈的机会，这和逛淘宝看韩剧一样，只是花时间并没有什么成长。反之，如果一个人太着急了，恨不得用精密的化学仪器衡量这些活动哪一个更对自己的发展有利，然后赶忙挑着这些高回报的活动去做。这样子的想法是不是有一些太急功近利了？成长，对于我，是一件长久而持续的事情，急不得，只能一点一点用心去做。我也相信，只有一定数量的累计才能保证当质的飞跃的机会来临的时候不会把握不住。</p><p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 2em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\">我不期待点金石一般的突破，我更相信时间看得见。旅行，对于我而言，就是一场用时间买下的股票，它本身有价值，但是我能不能赚，全看我怎么玩，什么时候玩，在哪里玩。我现在越来越发现，一个人如果在其本身的生活中死气沉沉，就很难在旅行中变得富有趣味。旅行是一面放大镜，把你灵魂中的好奇和鉴赏力发挥出来，而不是一个从无到有的科学创作。旅行即是生命长久累积的手段，其质量也是一个人积淀的体现。此生既然有幸降落在这颗美丽的星球上，就要尽自己所能去了解他，这是权力，也是责任。</p><p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 2em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\">Lovely planet和lonely planet的区别不只只是v和n，在我眼中，v是一个人往后缩的脚，n是一个人往前迈的脚。这颗星球是否美丽，全看自己怎么做。</p><p><br/></p>',4,0,'2018-12-21 17:22:04',6),(11,'点点滴滴','心情,回忆  ','连绵的秋雨下了近一个月了，我喜欢这个季节这样的天气，整个城市在朦胧的烟雨中显得格外清晰。','author',NULL,'<p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 2em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\">连绵的秋雨下了近一个月了，我喜欢这个季节这样的天气，整个城市在朦胧的烟雨中显得格外清晰。&nbsp;<br/></p><p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 2em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\">这个秋天很安静，除了淅沥的雨声和回忆，再没有其他的声音，也没有太多的思绪，是不是人心都得在经历了风雨之后才变得一尘不染。</p><p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 2em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\">何处合成愁，离人心上秋。无论身在何处，一阵秋风掠过，难免心生凄凉，那些聚少离多的人们，此刻是否也和我一样听着窗外的雨声，久久不能入眠。</p><p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 2em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\">汉中的秋季很短，回忆很长……</p><p><br/></p>',5,0,'2018-12-21 17:23:02',5),(12,'2014清明，晴。','生活,过往,摄影  ','','author','\\uploads/20181221\\8bb7c7d8a872d4ce450520e2a1e33ec7.jpg','<p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 2em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\">这两天生活中出现了一段小插曲，若隐若现的感觉到了春天的气息。后知后觉的我似乎已经丢失了接纳一个人的勇气，是对自己依然放不开？还是孤独已经变成一种习惯？</p><p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 2em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\">每当出现这样的情况，我心里都很清楚只要坚定的迈出一步，未来就会有不一样的色彩，可一到夜深人静的时候就会选择放手。从天而降的幸福，唯独我不能接，也接不住。</p><p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 2em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\">前段时间一朋友说我是抑郁症，非得给我吃什么精神病的药，还说如果不抓紧治疗就跟张国荣的结局一样，可到最后事实证明我的病没治好，她到是被我给说抑郁了。其实我真希望她说的是真的，这何尝不是一个完美的结局。</p><p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 2em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\">按照惯例是不是得上两张图，好吧，汉中天台山。</p><p><br/></p>',3,0,'2018-12-21 17:24:24',6),(13,'明心见性的一段对话','觉悟,生活,人生,规则  ','以下是我和妹子聊到房屋拆迁话题时的对话，希望我们能一直保持平常心，认真做自己……','author',NULL,'<p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 2em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\">以下是我和妹子聊到房屋拆迁话题时的对话，希望我们能一直保持平常心，认真做自己。</p><p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 0em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<strong>&nbsp;……</strong><br/></p><p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 0em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\"><span style=\"font-size: 12px;\">静待花开花落</span>&nbsp;：不管是什么，包包里有点钱钱才是王道。</p><p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 0em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\"><span style=\"font-size: 12px;\">　　 尤oοΟ○&nbsp;</span>：君子爱财，取之有道嘛，靠国家靠父母总归不是好事。</p><p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 0em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\"><span style=\"font-size: 12px;\">静待花开花落&nbsp;</span>：国家政策要来帮扶你，你还不愿意，好奇怪，应该积极配合，感谢共产党，才是对的吧。</p><p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 0em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\"><span style=\"font-size: 12px;\">　　 尤oοΟ○&nbsp;</span>：你看，又暴露了中华民族的劣根性了吧<img src=\"/ueditor/php/upload/image/20181221/1545384416.png\" alt=\"image\"/></p><p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 0em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\"><span style=\"font-size: 12px;\">　　 尤oοΟ○</span>&nbsp;：积极配合国家是正解的，但没必要感恩戴德，前者是顺应自然，后者是封建社会的奴才相。</p><p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 0em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\"><span style=\"font-size: 12px;\">　　 尤oοΟ○&nbsp;</span>：太多拆二代一夜暴富后骄奢淫逸，最后连命都搭上了，何必呢，不作不死。</p><p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 0em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\"><span style=\"font-size: 12px;\">静待花开花落</span>&nbsp;：哼哼</p><p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 0em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\"><span style=\"font-size: 12px;\">静待花开花落</span>&nbsp;：你愿意帮，我就响应政策，你不帮，我也不求你，是吧？</p><p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 0em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\"><span style=\"font-size: 12px;\">　 　尤oοΟ○</span>&nbsp;：对，就是平常心，没有希望就不会有希望达成后的自我膨胀。</p><p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 0em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\"><span style=\"font-size: 12px;\">　　 尤oοΟ○&nbsp;</span>：靠父母，靠父母官，靠朋友，靠国家，就他妈不靠自己，这就是为什么多数中国人在强势文化面前永远低人一等的原因。</p><p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 0em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\"><span style=\"font-size: 12px;\">静待花开花落</span>&nbsp;：我要靠自己，我还要占点便宜，怎么办？</p><p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 0em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\"><span style=\"font-size: 12px;\">　　 尤oοΟ○</span>&nbsp;：两者不可兼得，你要是真靠自己了，那便宜就不叫占了，努力得来的就不便宜。要是你侥幸占着便宜了，那便宜又有何用呢，只有贵的东西才会被人看重，贵的东西不是你想占就能占的。</p><p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 0em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\"><span style=\"font-size: 12px;\">　　 尤oοΟ○&nbsp;</span>：明白人都明白，患得患失的人始终活的不自在，不纠结这个了才，再往下就没话说了。</p><p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 0em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<strong>&nbsp;……</strong></p><p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 2em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\">我并不认为顺应自然，把握趋势有什么不对，任何通过努力得来的回报都是值得肯定的。但凡有点成就的人都不会从金钱多寡的角度去评价一个人。可为什么很多人被瞧不起呢，就是因为他们生性趋炎附势，蝇营狗苟，一脸的奴才相。</p><p><br/></p>',7,0,'2018-12-21 17:26:59',6),(14,'从丁元英到叶子农','觉悟,爱情,人生  ','很久没更新了，不是没时间不想写，而是懒，懒得打字，懒得思考，时间就这样悄然而逝……','author',NULL,'<p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 2em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\">很久没更新了，不是没时间不想写，而是懒，懒得打字，懒得思考，时间就这样悄然而逝。&nbsp;<br/></p><p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 2em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\">凉爽的下午，淡蓝的天空浮现出层层云雾，夕阳也逐渐失去了颜色，随着夜幕降临，白云变成了乌云，晚上多是瑟瑟的风伴着淅沥的雨，这是就是初秋的汉中。<br/></p><p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 2em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\">《天幕红尘》最后几页终于看完了，没怎么看懂，感觉完全不像豆豆的前两部作品的风格，字里行间刻意的成份太多，情节也过于生硬。书中的见路不走似乎有《遥远的救世主》的影子，只是一个更接近俗世，一个居高临下，用了两种方法体现了一个明白人的包容。不过我更喜欢元英和小丹的爱情，其实说是爱情都有些着相，是人的本性才对，彼此还没开口就知道对方想要什么，少了平常人之间的猜嫉，剩下的就是能不能给的问题了。当然，也不排除元英的确是个调情高手，就这样当下随缘了。</p><p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 2em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\">我们每个人都在自己的人生道路上辗转前行，见路不走是一种觉悟，遥远的救世主是一种境界。</p><p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 2em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\">书评就不写了，读的书本来就不多，怎么评都是主观臆断。</p><p><br/></p>',7,0,'2018-12-21 17:28:11',5),(15,'五十度灰的天空','觉悟,生活,规则  ','此刻窗外的天空还蒙着一层50度的灰，春天迁徙过来家燕已经孵出了一窝小燕子，在外面叽叽喳喳的叫着，朝气蓬勃。可是到了秋天又有几只小燕子跟随妈妈南去呢，这不得而知，我只看见有的还没学会飞就不幸夭折，有的再夏季的狂风暴雨中无力挣扎，这就是命，是规律 ……','author','\\uploads/20181221\\8b36f8c2fcd8daa5e092dac76b1ff982.jpg','<p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 2em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\">现在是凌晨四点多，最近不知为何，一天比一天醒的早，不是被外面的鸟叫声叫醒，就是从梦中惊醒，更多的是后者，就像现在。</p><p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 2em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\">一晚上做了一个梦，梦的有些乱但真实，她们说梦境与现实是反的，我也希望如此。一直以来我都认为自己是一个具有B型人格的人，平常心，不强求，不易为外界事物影响。然而我慢慢发现近一年多的生活中我有A型人格行为，而且愈演愈烈。做事追求目标和效率，进取心强，急功近利，总想做到最好。我知道这并不是我的本质，只是随着年龄的增长，受大多数人价值观的影响，开始变得有压力。据说A型人格特质的人生命相对较短，难怪一直感觉身体不适。</p><p style=\"margin-top: 10px; margin-bottom: 15px; padding: 0px; text-indent: 2em; color: rgb(85, 85, 85); font-family: 微软雅黑, 宋体, &quot;Trebuchet MS&quot;, Tahoma, Arial, sans-serif; font-size: 14px; text-align: justify; white-space: normal;\">此刻窗外的天空还蒙着一层五十度的灰，春天迁徙过来家燕已经孵出了一窝小燕子，在外面叽叽喳喳的叫着，朝气蓬勃。可是到了秋天又有几只小燕子跟随妈妈南去呢，这不得而知，我只看见有的还没学会飞就不幸夭折，有的再夏季的狂风暴雨中无力挣扎，这就是命，是规律。</p><p><br/></p>',6,0,'2018-12-21 17:29:33',5);

/*Table structure for table `bk_auth_group` */

DROP TABLE IF EXISTS `bk_auth_group`;

CREATE TABLE `bk_auth_group` (
  `id` mediumint(8) unsigned NOT NULL AUTO_INCREMENT COMMENT '用户组id',
  `title` char(100) NOT NULL DEFAULT '' COMMENT '用户组名称',
  `status` tinyint(1) NOT NULL DEFAULT '1' COMMENT '开启状态默认1 开启 0关闭',
  `rules` varchar(255) NOT NULL DEFAULT '' COMMENT '权限内容',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=3 DEFAULT CHARSET=utf8;

/*Data for the table `bk_auth_group` */

insert  into `bk_auth_group`(`id`,`title`,`status`,`rules`) values (1,'超级管理员',1,''),(2,'管理员',1,'2,16,33,34,35,17,18,19,21,22,42,43,44,45,46');

/*Table structure for table `bk_auth_group_access` */

DROP TABLE IF EXISTS `bk_auth_group_access`;

CREATE TABLE `bk_auth_group_access` (
  `uid` mediumint(8) unsigned NOT NULL COMMENT '管理员id',
  `group_id` mediumint(8) unsigned NOT NULL COMMENT '用户组id',
  UNIQUE KEY `uid_group_id` (`uid`,`group_id`),
  KEY `uid` (`uid`),
  KEY `group_id` (`group_id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

/*Data for the table `bk_auth_group_access` */

insert  into `bk_auth_group_access`(`uid`,`group_id`) values (1,1),(2,2);

/*Table structure for table `bk_auth_rule` */

DROP TABLE IF EXISTS `bk_auth_rule`;

CREATE TABLE `bk_auth_rule` (
  `id` mediumint(8) unsigned NOT NULL AUTO_INCREMENT COMMENT '权限id',
  `name` char(80) NOT NULL DEFAULT '' COMMENT '控制器方法名称',
  `title` char(20) NOT NULL DEFAULT '' COMMENT '权限名称',
  `type` tinyint(1) NOT NULL DEFAULT '1' COMMENT '权限类型',
  `status` tinyint(1) NOT NULL DEFAULT '1' COMMENT '权限状态',
  `condition` char(100) NOT NULL DEFAULT '',
  `pid` mediumint(9) NOT NULL DEFAULT '0' COMMENT '上级id',
  `level` tinyint(4) NOT NULL DEFAULT '0' COMMENT '权限级别',
  PRIMARY KEY (`id`),
  UNIQUE KEY `name` (`name`)
) ENGINE=MyISAM AUTO_INCREMENT=47 DEFAULT CHARSET=utf8;

